import { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.module.css"
const DOG_MASCOT =
  "https://cdn.pixabay.com/photo/2016/03/31/19/14/dog-1298135_1280.png";

const bubbles = [
  { top: "10%", left: "5%", size: 60, anim: "float0" },
  { top: "22%", left: "85%", size: 70, anim: "float1" },
  { top: "34%", left: "5%", size: 80, anim: "float2" },
  { top: "46%", left: "85%", size: 90, anim: "float3" },
  { top: "58%", left: "5%", size: 100, anim: "float4" },
  { top: "70%", left: "85%", size: 110, anim: "float5" }
];

const Login = () => {
  const [data, setData] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = "http://localhost:8080/api/auth";
      const { data: res } = await axios.post(url, data);
      localStorage.setItem("token", res.data);
      window.location = "/";
    } catch (error) {
      if (
        error.response &&
        error.response.status >= 400 &&
        error.response.status <= 500
      ) {
        setError(error.response.data.message);
      }
    }
  };

  return (
    <div className="login-bg">
      {/* Animated floating bubbles */}
      {bubbles.map((b, i) => (
        <div
          key={i}
          className="login-bubble"
          style={{
            top: b.top,
            left: b.left,
            width: b.size,
            height: b.size,
            background: "rgba(185,131,255,0.12)",
            animationName: b.anim
          }}
        />
      ))}

      <div className="card login-card">
        {/* Left: Mascot + Welcome */}
        <div className="login-mascot-section">
          <img
            src={DOG_MASCOT}
            alt="Dog Mascot"
            className="login-mascot-img"
          />
          <h2 className="login-mascot-title">
            Witaj w PuppyParadise!
          </h2>
          <p className="login-mascot-desc">
            Najlepszy salon dla Twojego pupila.<br />
            Zaloguj się i zadbaj o swojego psa!
          </p>
        </div>
        {/* Right: Login form */}
        <div className="login-form-section">
          <form onSubmit={handleSubmit}>
            <h1 className="login-title">
              Logowanie
            </h1>
            <input
              type="email"
              placeholder="Email"
              name="email"
              onChange={handleChange}
              value={data.email}
              required
              className="login-input form-control"
            />
            <input
              type="password"
              placeholder="Hasło"
              name="password"
              onChange={handleChange}
              value={data.password}
              required
              className="login-input form-control"
            />
            {error && (
              <div className="login-error">
                {error}
              </div>
            )}
            <button
              type="submit"
              className="login-btn"
            >
              Zaloguj się
            </button>
          </form>
          <div className="login-signup-row">
            <span style={{ color: "#a084ca", fontWeight: 500 }}>Nie masz konta?</span>
            <Link to="/signup" className="login-signup-link">
              Zarejestruj się
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
