import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AppointmentHistory = ({ userId }) => {
  const [appointments, setAppointments] = useState([]);
  const [activeTab, setActiveTab] = useState('upcoming');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchAppointments = async (status = 'all') => {
    setLoading(true);
    setError('');

    try {
      const response = await axios.get(`/api/appointments/user/${userId}?status=${status}`);
      setAppointments(response.data.appointments);
    } catch (error) {
      setError('Błąd podczas ładowania historii wizyt');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAppointments(activeTab);
  }, [activeTab, userId]);

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('pl-PL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getStatusBadge = (appointment) => {
    const appointmentDate = new Date(`${appointment.date}T${appointment.timeSlot}:00`);
    const now = new Date();

    if (appointmentDate > now) {
      return <span className="badge bg-primary">Zaplanowana</span>;
    } else {
      return <span className="badge bg-secondary">Zakończona</span>;
    }
  };

  return (
    <div className="appointment-history">
      <div className="card">
        <div className="card-header">
          <h4>Historia Wizyt</h4>

          {/* Tab Navigation */}
          <ul className="nav nav-tabs card-header-tabs">
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'upcoming' ? 'active' : ''}`}
                onClick={() => setActiveTab('upcoming')}
              >
                Nadchodzące
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'past' ? 'active' : ''}`}
                onClick={() => setActiveTab('past')}
              >
                Przeszłe
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'all' ? 'active' : ''}`}
                onClick={() => setActiveTab('all')}
              >
                Wszystkie
              </button>
            </li>
          </ul>
        </div>

        <div className="card-body">
          {error && (
            <div className="alert alert-danger">{error}</div>
          )}

          {loading ? (
            <div className="text-center">
              <div className="spinner-border" role="status">
                <span className="visually-hidden">Ładowanie...</span>
              </div>
            </div>
          ) : (
            <AppointmentList appointments={appointments} />
          )}
        </div>
      </div>
    </div>
  );
};
