import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from 'axios';

const SERVICES = [
    { label: "Kpiel i suszenie", value: "Kpiel i suszenie" },
    { label: "Strzy偶enie", value: "Strzy偶enie" },
    { label: "Trymowanie", value: "Trymowanie" },
    { label: "Obcinanie pazur贸w", value: "Obcinanie pazur贸w" },
    { label: "Czyszczenie uszu", value: "Czyszczenie uszu" }
];

const lavender = {
    light: "#f3e8ff",
    main: "#b983ff",
    dark: "#7f53ac",
    accent: "#a084ca"
};

const Main = () => {
    const [name, setName] = useState("");
    const [date, setDate] = useState("");
    const [phone, setPhone] = useState("");
    const [selectedServices, setSelectedServices] = useState([]);
    const [note, setNote] = useState("");
    const [error, setError] = useState("");

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    const handleCheckboxChange = (service) => {
        setSelectedServices((prev) =>
            prev.includes(service)
                ? prev.filter((s) => s !== service)
                : [...prev, service]
        );
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (selectedServices.length === 0) {
            setError("Wybierz przynajmniej jedn usug.");
            return;
        }
        try {
            const response = await axios.post('/api/appointments', {
                name,
                date,
                phone,
                note,
                services: selectedServices
            })
            setName("");
            setDate("");
            setPhone("");
            setSelectedServices([]);
            setNote("");
            setError("")
        } catch (error) {
            if (error.response && error.response.data.message) {
                setError(error.response.data.message);
            } else {
                setError("Wystpi bd. Spr贸buj ponownie.")
            }
        }
    };

    return (
        <div
            style={{
                minHeight: "100vh",
                background: `linear-gradient(135deg, ${lavender.light} 0%, ${lavender.main} 100%)`
            }}
            className="d-flex flex-column"
        >
            <nav
                className="navbar navbar-expand-lg shadow-sm"
                style={{ background: lavender.dark }}
            >
                <div className="container">
                    <h1 className="navbar-brand mb-0" style={{ color: "#fff" }}>
                        <span role="img" aria-label="dog"></span> Szczliwy Piesek
                    </h1>
                    <button
                        className="btn"
                        style={{
                            background: lavender.accent,
                            color: "#fff",
                            border: "none"
                        }}
                        onClick={handleLogout}
                    >
                        Wyloguj
                    </button>
                </div>
            </nav>
            <div className="container flex-grow-1 d-flex align-items-center justify-content-center">
                <div className="col-md-6">
                    <div
                        className="card shadow rounded p-4 mt-5"
                        style={{
                            background: "#fff",
                            border: `2px solid ${lavender.main}`,
                            boxShadow: `0 4px 24px 0 ${lavender.main}33`
                        }}
                    >
                        <div className="text-center mb-4">
                            <img
                                src="https://cdn-icons-png.flaticon.com/512/616/616408.png"
                                alt="Dog Icon"
                                width={64}
                                className="mb-2"
                                style={{
                                    filter: `drop-shadow(0 0 8px ${lavender.main}66)`
                                }}
                            />
                            <h2
                                className="card-title mb-0"
                                style={{ color: lavender.dark }}
                            >
                                Um贸w wizyt
                            </h2>
                        </div>
                        {error && (
                            <div className="alert alert-danger">{error}</div>
                        )}
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label className="form-label" style={{ color: lavender.dark }}>
                                    Imi psa:
                                </label>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={name}
                                    placeholder="Podaj imi psa"
                                    onChange={(e) => setName(e.target.value)}
                                    required
                                    style={{ borderColor: lavender.main }}
                                />
                            </div>
                            <div className="mb-3">
                                <label className="form-label" style={{ color: lavender.dark }}>
                                    Data:
                                </label>
                                <div className="input-group">
                                    <span
                                        className="input-group-text"
                                        style={{ background: lavender.light, color: lavender.dark }}
                                    >
                                        <i className="bi bi-calendar-event"></i>
                                    </span>
                                    <input
                                        type="date"
                                        className="form-control"
                                        value={date}
                                        onChange={(e) => setDate(e.target.value)}
                                        required
                                        style={{ borderColor: lavender.main }}
                                    />
                                </div>
                            </div>
                            <div className="mb-3">
                                <label className="form-label" style={{ color: lavender.dark }}>
                                    Numer telefonu:
                                </label>
                                <input
                                    type="tel"
                                    className="form-control"
                                    value={phone}
                                    placeholder="Podaj numer telefonu"
                                    onChange={(e) => setPhone(e.target.value)}
                                    required
                                    style={{ borderColor: lavender.main }}
                                />
                            </div>
                            <fieldset className="mb-3">
                                <legend
                                    className="col-form-label pt-0"
                                    style={{ color: lavender.dark }}
                                >
                                    Wybierz usugi:
                                </legend>
                                <div className="row">
                                    {SERVICES.map((service) => (
                                        <div className="col-6" key={service.value}>
                                            <div className="form-check">
                                                <input
                                                    className="form-check-input"
                                                    type="checkbox"
                                                    id={service.value}
                                                    checked={selectedServices.includes(service.value)}
                                                    onChange={() => handleCheckboxChange(service.value)}
                                                    style={{
                                                        accentColor: lavender.main
                                                    }}
                                                />
                                                <label
                                                    className="form-check-label"
                                                    htmlFor={service.value}
                                                    style={{ color: lavender.dark }}
                                                >
                                                    {service.label}
                                                </label>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </fieldset>
                            <div className="mb-3">
                                <label className="form-label" style={{ color: lavender.dark }}>
                                    Dodatkowe uwagi:
                                </label>
                                <textarea
                                    className="form-control"
                                    value={note}
                                    placeholder="Wpisz dodatkowe informacje lub uwagi"
                                    onChange={(e) => setNote(e.target.value)}
                                    style={{ borderColor: lavender.main }}
                                />
                            </div>
                            <button
                                type="submit"
                                className="btn w-100 shadow-sm"
                                style={{
                                    background: lavender.main,
                                    color: "#fff",
                                    fontWeight: "bold",
                                    border: "none"
                                }}
                            >
                                Um贸w wizyt
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
