import { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const lavender = {
    light: "#f3e8ff",
    main: "#b983ff",
    dark: "#7f53ac",
    accent: "#a084ca"
};

const Login = () => {
    const [data, setData] = useState({ email: "", password: "" });
    const [error, setError] = useState("");
    const handleChange = ({ currentTarget: input }) => {
        setData({ ...data, [input.name]: input.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const url = "http://localhost:8080/api/auth";
            const { data: res } = await axios.post(url, data);
            localStorage.setItem("token", res.data);
            window.location = "/";
        } catch (error) {
            if (
                error.response &&
                error.response.status >= 400 &&
                error.response.status <= 500
            ) {
                setError(error.response.data.message);
            }
        }
    };

    return (
        <div
            style={{
                minHeight: "100vh",
                background: `linear-gradient(135deg, ${lavender.light} 0%, ${lavender.main} 100%)`
            }}
            className="d-flex align-items-center justify-content-center"
        >
            <div
                className="card shadow rounded p-4"
                style={{
                    maxWidth: 800,
                    width: "100%",
                    display: "flex",
                    flexDirection: "row",
                    border: `2px solid ${lavender.main}`,
                    boxShadow: `0 4px 24px 0 ${lavender.main}33`
                }}
            >
                {/* Left side: Login form */}
                <div className="flex-fill p-4" style={{ minWidth: 300 }}>
                    <form onSubmit={handleSubmit}>
                        <h1 className="mb-4" style={{ color: lavender.dark }}>
                            Login to Your Account
                        </h1>
                        <input
                            type="email"
                            placeholder="Email"
                            name="email"
                            onChange={handleChange}
                            value={data.email}
                            required
                            className="form-control mb-3"
                            style={{
                                borderColor: lavender.main,
                                background: lavender.light
                            }}
                        />
                        <input
                            type="password"
                            placeholder="Password"
                            name="password"
                            onChange={handleChange}
                            value={data.password}
                            required
                            className="form-control mb-3"
                            style={{
                                borderColor: lavender.main,
                                background: lavender.light
                            }}
                        />
                        {error && (
                            <div
                                className="alert alert-danger py-2"
                                style={{
                                    background: lavender.light,
                                    color: lavender.dark,
                                    borderColor: lavender.accent
                                }}
                            >
                                {error}
                            </div>
                        )}
                        <button
                            type="submit"
                            className="btn w-100 mb-2"
                            style={{
                                background: lavender.main,
                                color: "#fff",
                                fontWeight: "bold",
                                border: "none"
                            }}
                        >
                            Sign In
                        </button>
                    </form>
                </div>
                {/* Right side: Sign up prompt */}
                <div
                    className="flex-fill p-4 d-flex flex-column align-items-center justify-content-center"
                    style={{
                        background: lavender.light,
                        borderLeft: `2px solid ${lavender.main}`,
                        minWidth: 300
                    }}
                >
                    <h1 className="mb-3" style={{ color: lavender.dark }}>
                        New Here?
                    </h1>
                    <Link to="/signup" style={{ width: "100%" }}>
                        <button
                            type="button"
                            className="btn w-100"
                            style={{
                                background: "#fff",
                                color: lavender.dark,
                                border: `2px solid ${lavender.main}`,
                                fontWeight: "bold"
                            }}
                        >
                            Sign Up
                        </button>
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default Login;
