import React, { useState } from "react";
import styles from "./Main.module.css";

const SERVICES = [
    { label: "Washing", value: "washing" },
    { label: "Trimming", value: "trimming" },
    { label: "Brushing", value: "brushing" },
    { label: "Nail Clipping", value: "nail_clipping" }
];

const Main = () => {
    const [name, setName] = useState("");
    const [date, setDate] = useState("");
    const [selectedServices, setSelectedServices] = useState([]);

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    const handleCheckboxChange = (service) => {
        setSelectedServices((prev) =>
            prev.includes(service)
                ? prev.filter((s) => s !== service)
                : [...prev, service]
        );
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        alert(
            `Appointment for ${name} on ${date}.\nServices: ${selectedServices.join(", ")}`
        );
        setName("");
        setDate("");
        setSelectedServices([]);
    };

    return (
        <div className={styles.lavender-bg}>
            <nav className={`navbar navbar-expand-lg ${styles.lavender-navbar} mb-4`}>
                <div className="container-fluid">
                    <h1 className={`navbar-brand mb-0 ${styles.lavender-title}`}>MySite</h1>
                    <button className={`btn ${styles.lavender-btn-logout}`} onClick={handleLogout}>
                        Logout
                    </button>
                </div>
            </nav>
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className={`card shadow-sm ${styles.lavender-card}`}>
                        <div className="card-body">
                            <h2 className={`card-title mb-4 ${styles.lavender-title}`}>Book an Appointment</h2>
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label className={`form-label ${styles.lavender-label}`}>Name:</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className={`form-label ${styles.lavender-label}`}>Date:</label>
                                    <input
                                        type="date"
                                        className="form-control"
                                        value={date}
                                        onChange={(e) => setDate(e.target.value)}
                                        required
                                    />
                                </div>
                                <fieldset className="mb-3">
                                    <legend className={`col-form-label pt-0 ${styles.lavender-label}`}>Select Services:</legend>
                                    {SERVICES.map((service) => (
                                        <div className={`form-check ${styles.lavender-checkbox}`} key={service.value}>
                                            <input
                                                className="form-check-input"
                                                type="checkbox"
                                                id={service.value}
                                                checked={selectedServices.includes(service.value)}
                                                onChange={() => handleCheckboxChange(service.value)}
                                            />
                                            <label
                                                className="form-check-label"
                                                htmlFor={service.value}
                                            >
                                                {service.label}
                                            </label>
                                        </div>
                                    ))}
                                </fieldset>
                                <button type="submit" className={`btn ${styles.lavender-btn-primary} w-100`}>
                                    Book Appointment
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
