import React, { useState } from "react"
import styles from "./styles.module.css"

const SERVICES = [
    { label: "Washing", value: "washing" },
    { label: "Trimming", value: "trimming" },
    { label: "Brushing", value: "brushing" },
    { label: "Nail Clipping", value: "nail_clipping" }
]

const Main = () => {
    const [name, setName] = useState("")
    const [date, setDate] = useState("")
    const [selectedServices, setSelectedServices] = useState([])

    const handleLogout = () => {
        localStorage.removeItem("token")
        window.location.reload()
    }

    const handleCheckboxChange = (service) => {
        setSelectedServices((prev) =>
            prev.includes(service)
                ? prev.filter((s) => s !== service)
                : [...prev, service]
        )
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        // Here you would send the data to your backend API
        alert(
            `Appointment for ${name} on ${date}.\nServices: ${selectedServices.join(", ")}`
        )
        // Reset form
        setName("")
        setDate("")
        setSelectedServices([])
    }

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <h1>MySite</h1>
                <button className={styles.white_btn} onClick={handleLogout}>
                    Logout
                </button>
            </nav>
            <div className={styles.form_container}>
                <h2>Book an Appointment</h2>
                <form className={styles.appointment_form} onSubmit={handleSubmit}>
                    <label>
                        Name:
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    </label>
                    <label>
                        Date:
                        <input
                            type="date"
                            value={date}
                            onChange={(e) => setDate(e.target.value)}
                            required
                        />
                    </label>
                    <fieldset>
                        <legend>Select Services:</legend>
                        {SERVICES.map((service) => (
                            <label key={service.value} className={styles.checkbox_label}>
                                <input
                                    type="checkbox"
                                    checked={selectedServices.includes(service.value)}
                                    onChange={() => handleCheckboxChange(service.value)}
                                />
                                {service.label}
                            </label>
                        ))}
                    </fieldset>
                    <button type="submit" className={styles.white_btn}>
                        Book Appointment
                    </button>
                </form>
            </div>
        </div>
    )
}

export default Main
