import { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

// Color palette
const PALETTE = {
  lavender_bg: "#ede6fa",
  lavender: "#b983ff",
  brown: "#8b4513",
  brown_light: "#d2b48c",
  white: "#fff"
};

const DOG_MASCOT =
  "https://cdn.pixabay.com/photo/2016/03/31/19/14/dog-1298135_1280.png";

const Login = () => {
  const [data, setData] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = "http://localhost:8080/api/auth";
      const { data: res } = await axios.post(url, data);
      localStorage.setItem("token", res.data);
      window.location = "/";
    } catch (error) {
      if (
        error.response &&
        error.response.status >= 400 &&
        error.response.status <= 500
      ) {
        setError(error.response.data.message);
      }
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: PALETTE.lavender_bg,
        display: "flex",
        alignItems: "center",
        justifyContent: "center"
      }}
    >
      <div
        className="card shadow p-4 border-0"
        style={{
          maxWidth: 400,
          width: "100%",
          borderRadius: 24,
          background: PALETTE.white
        }}
      >
        <div className="text-center mb-4">
          <img
            src={DOG_MASCOT}
            alt="Dog Mascot"
            style={{
              width: 80,
              height: 80,
              objectFit: "contain",
              borderRadius: "50%",
              background: PALETTE.lavender_bg,
              border: `2px solid ${PALETTE.lavender}`
            }}
          />
          <h2 className="mt-3 mb-1 fw-bold" style={{ color: PALETTE.brown }}>
            PuppyParadise
          </h2>
          <p className="text-muted" style={{ fontSize: 15 }}>
            Zaloguj się, by zadbać o swojego pupila!
          </p>
        </div>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            placeholder="Email"
            name="email"
            onChange={handleChange}
            value={data.email}
            required
            className="form-control mb-3 py-2"
            style={{
              borderColor: PALETTE.lavender,
              background: PALETTE.lavender_bg,
              borderRadius: 12
            }}
          />
          <input
            type="password"
            placeholder="Hasło"
            name="password"
            onChange={handleChange}
            value={data.password}
            required
            className="form-control mb-3 py-2"
            style={{
              borderColor: PALETTE.brown_light,
              background: PALETTE.lavender_bg,
              borderRadius: 12
            }}
          />
          {error && (
            <div
              className="alert alert-danger py-2"
              style={{
                background: PALETTE.lavender_bg,
                color: PALETTE.brown,
                borderColor: PALETTE.brown,
                borderRadius: 10
              }}
            >
              {error}
            </div>
          )}
          <button
            type="submit"
            className="btn w-100 mb-2 fw-bold"
            style={{
              background: PALETTE.brown,
              color: "#fff",
              borderRadius: 12,
              fontSize: 18
            }}
          >
            Zaloguj się
          </button>
        </form>
        <div className="text-center mt-3">
          <span style={{ color: PALETTE.brown, fontWeight: 500 }}>
            Nie masz konta?
          </span>
          <Link to="/signup" className="ms-2">
            <button
              type="button"
              className="btn"
              style={{
                background: PALETTE.lavender,
                color: "#fff",
                border: "none",
                fontWeight: "bold",
                borderRadius: 12
              }}
            >
              Zarejestruj się
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
