import React, { useEffect, useState } from "react";
import axios from "axios";
import "./AppointmentHistory.css";

const AppointmentHistory = () => {
  const [past, setPast] = useState([]);
  const [upcoming, setUpcoming] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("upcoming");

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    setLoading(true);
    setError("");

    try {
      const response = await axios.get("/api/appointments/history", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        }
      });

      // Separate appointments into past and upcoming
      const now = new Date();
      const pastAppointments = [];
      const upcomingAppointments = [];

      response.data.appointments.forEach(appointment => {
        const appointmentDate = new Date(`${appointment.date}T${appointment.timeSlot}:00`);
        if (appointmentDate < now) {
          pastAppointments.push(appointment);
        } else {
          upcomingAppointments.push(appointment);
        }
      });

      setPast(pastAppointments);
      setUpcoming(upcomingAppointments);
    } catch (error) {
      if (error.response?.status === 401) {
        setError("Sesja wygas≈Ça. Zaloguj siƒô ponownie.");
        setTimeout(() => {
          localStorage.removeItem('token');
          window.location.href = '/login';
        }, 2000);
      } else {
        setError("B≈ÇƒÖd podczas ≈Çadowania historii wizyt");
      }
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('pl-PL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getStatusBadge = (appointment) => {
    const appointmentDate = new Date(`${appointment.date}T${appointment.timeSlot}:00`);
    const now = new Date();

    if (appointmentDate > now) {
      return <span className="status-badge upcoming">Zaplanowana</span>;
    } else {
      return <span className="status-badge completed">Zako≈Ñczona</span>;
    }
  };

  const getCurrentAppointments = () => {
    switch (activeTab) {
      case 'upcoming':
        return upcoming;
      case 'past':
        return past;
      default:
        return [...upcoming, ...past];
    }
  };

  return (
    <div className="appointment-history-container">
      <div className="history-header">
        <h2>Historia Wizyt</h2>

        {/* Summary Cards */}
        <div className="summary-cards">
          <div className="summary-card">
            <div className="summary-number">{upcoming.length}</div>
            <div className="summary-label">NadchodzƒÖce</div>
          </div>
          <div className="summary-card">
            <div className="summary-number">{past.length}</div>
            <div className="summary-label">Przesz≈Çe</div>
          </div>
          <div className="summary-card">
            <div className="summary-number">{upcoming.length + past.length}</div>
            <div className="summary-label">≈ÅƒÖcznie</div>
          </div>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="tab-navigation">
        <button 
          className={`tab-button ${activeTab === 'upcoming' ? 'active' : ''}`}
          onClick={() => setActiveTab('upcoming')}
        >
          NadchodzƒÖce ({upcoming.length})
        </button>
        <button 
          className={`tab-button ${activeTab === 'past' ? 'active' : ''}`}
          onClick={() => setActiveTab('past')}
        >
          Przesz≈Çe ({past.length})
        </button>
        <button 
          className={`tab-button ${activeTab === 'all' ? 'active' : ''}`}
          onClick={() => setActiveTab('all')}
        >
          Wszystkie ({upcoming.length + past.length})
        </button>
      </div>

      {/* Content Area */}
      <div className="history-content">
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>≈Åadowanie historii wizyt...</p>
          </div>
        ) : (
          <AppointmentList appointments={getCurrentAppointments()} />
        )}
      </div>
    </div>
  );
};

const AppointmentList = ({ appointments }) => {
  if (appointments.length === 0) {
    return (
      <div className="empty-state">
        <div className="empty-icon">üìÖ</div>
        <h3>Brak wizyt</h3>
        <p>Nie masz jeszcze ≈ºadnych wizyt w tej kategorii.</p>
      </div>
    );
  }

  return (
    <div className="appointments-grid">
      {appointments.map((appointment) => (
        <AppointmentCard key={appointment._id} appointment={appointment} />
      ))}
    </div>
  );
};

const AppointmentCard = ({ appointment }) => {
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('pl-PL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getStatusBadge = (appointment) => {
    const appointmentDate = new Date(`${appointment.date}T${appointment.timeSlot}:00`);
    const now = new Date();

    if (appointmentDate > now) {
      return <span className="status-badge upcoming">Zaplanowana</span>;
    } else {
      return <span className="status-badge completed">Zako≈Ñczona</span>;
    }
  };

  return (
    <div className="appointment-card">
      <div className="card-header">
        <div className="pet-info">
          <h3 className="pet-name">{appointment.name}</h3>
          <span className="phone-number">{appointment.phone}</span>
        </div>
        {getStatusBadge(appointment)}
      </div>

      <div className="appointment-details">
        <div className="date-time">
          <div className="date">
            <span className="icon">üìÖ</span>
            <strong>{formatDate(appointment.date)}</strong>
          </div>
          <div className="time">
            <span className="icon">üïê</span>
            <span>{appointment.timeSlot}</span>
          </div>
        </div>

        <div className="services-section">
          <h4>Us≈Çugi:</h4>
          <ul className="services-list">
            {appointment.services.map((service, index) => (
              <li key={index} className="service-item">
                <span className="service-bullet">‚Ä¢</span>
                {service}
              </li>
            ))}
          </ul>
        </div>

        <div className="duration">
          <span className="icon">‚è±Ô∏è</span>
          <span>{appointment.duration} minut</span>
        </div>

        {appointment.note && (
          <div className="note-section">
            <h4>Notatka:</h4>
            <p className="note-text">{appointment.note}</p>
          </div>
        )}
      </div>

      <div className="card-footer">
        <small className="created-date">
          Utworzono: {new Date(appointment.createdAt).toLocaleDateString('pl-PL')}
        </small>
      </div>
    </div>
  );
};

export default AppointmentHistory;
